import {createSlice} from '@reduxjs/toolkit'
const initialState = {
    isLoading: false,
    loggedIn: false,
    error:null,
    user: null,
}
export const authSlice = createSlice({
    name: 'auth',
    initialState ,
    reducers: {
        loginUserStart: (state) => {
            state.isLoading = true
        },
        loginUserSuccess: (state) => {
           state.loggedIn = true 
           state.isLoading = false 
        },
        loginUserFailure: (state) => {
            state.isLoading = false
            state.error = "error"
        },
        registerStart: (state) => {
            state.isLoading = true
        },
        registerSuccess: (state) => {
           state.loggedIn = true 
           state.isLoading = false 
        },
        registerFailure: (state) => {
            state.isLoading = false
            state.error = "error"


        },
    }

})

export const {loginUserStart , loginUserSuccess,  loginUserFailure , registerStart , registerSuccess , registerFailure} = authSlice.actions
export default authSlice.reducer
