import { useCallback } from 'react'
import { useSelector } from 'react-redux'

const ValidationError = () => { 
  const { error } = useSelector(state => state.auth)

  const errorMessages = useCallback(() => {
    if (!error || typeof error !== 'object') return [] // Agar error obyekt bo'lmasa, bo'sh massiv qaytariladi.

    return Object.keys(error).map(name => {
      const value = error[name]
      // Agar value massiv bo'lsa, join() ishlatiladi, aks holda string sifatida qaytariladi
      if (Array.isArray(value)) {
        return value.join(', ')
      }
      return String(value) // Agar massiv bo'lmasa, stringga aylantiriladi
    })
  }, [error])

  const messages = errorMessages()

  return (
    error && messages.length > 0 && (
      <div>
        {messages.map((msg, index) => (
          <p key={index}>{msg}</p>
        ))}
      </div>
    )
  )
}

export default ValidationError
