import { useCallback } from 'react'
import { useSelector } from 'react-redux'

const ValidationError = () => { 
  const {error} = useSelector(state =>state.auth)
  console.log(error)
 const errorMessages = useCallback(() => {
    if (!error) return []; // agar error null bo'lsa, bo'sh array qaytar
    return Object.keys(error).map(name => {
      // agar error[name] array bo'lsa join qilamiz, aks holda stringni bevosita ishlatamiz
      const msg = Array.isArray(error[name]) ? error[name].join(', ') : error[name];
      return `${name} : ${msg}`;
    });
  }, [error]);

  return (error !== null && errorMessages())
//   return (error !== null && <div>Validation Error</div>)?
}

export default ValidationError
